import React, { useState, useEffect } from 'react';

function App() {
    const [data, setData] = useState([{}]);
    const [activeTab, setActiveTab] = useState('faceRecognition');

    useEffect(() => {
        fetch('/members')
            .then((res) => res.json())
            .then((data) => setData(data));
    }, []);

    const handleTabChange = (tab) => {
        setActiveTab(tab);
    };

    return (
        <div>
            <div className="dashboard">
                <div className="cameraDisplay">
                    {/* Camera display component */}
                </div>
                <div className="tabs">
                    <button
                        className={activeTab === 'faceRecognition' ? 'active' : ''}
                        onClick={() => handleTabChange('faceRecognition')}
                    >
                        Face Recognition
                    </button>
                    <button
                        className={activeTab === 'objectDetection' ? 'active' : ''}
                        onClick={() => handleTabChange('objectDetection')}
                    >
                        Object Detection
                    </button>
                    <button
                        className={activeTab === 'behaviorDetection' ? 'active' : ''}
                        onClick={() => handleTabChange('behaviorDetection')}
                    >
                        Behavior Detection
                    </button>
                </div>
                <div className="notificationBox">
                    {/* Notification box component */}
                </div>
                <div className="alertsBox">
                    {/* Alerts box component */}
                </div>
            </div>
        </div>
    );
}

export default App;
